import React, { Component } from 'react';

class Counter extends Component {
  constructor(props) {
    super(props);
    //state의 초깃값 설정하기 
    this.state = {
      number: 0
    };
  }

  render() {
    const { number } = this.state; // state를 조회할 때는 this.state를 사용 
    return (
      <div>
        <h1>{number}</h1>
        <button
          /* onClick을 통해 버튼이 클릭되었을 때 호출할 함수를 지정합니다.*/
          onClick={() => {
            this.setState({ number: number + 1 });
          }}
        >
          +1
        </button>
      </div>
    );
  }
}

export default Counter;
